mapper.select:
SELECT      t.travel_id , t.travel_name , CONCAT(t.start_date, '~' ,t.end_date) period,
            DATEDIFF(DATE_FORMAT(t.start_date , '%Y-%m-%d.') , DATE_FORMAT(now() ,'%Y-%m-%d.')) d_day
FROM        travel t , `user` u
WHERE       t.user_id = u.user_id
and         u.nick_name = #{nickName}
ORDER BY    t.start_date ASC;

mapper.insert:
INSERT into travel_schedule (travel_id, destination_id, `day`, route_index) select 1 as travel_id, A.destination_id , A.day, @rownum:=@rownum+1 as route_index, A.tag_name from ( SELECT 1, d.destination_id, 1 as day, t.tag_name FROM destination d, travel_schedule ts, destiantion_tag_list dtl, tag t, (SELECT @rownum:=0) TMP where d.user_id =1 and dtl.tag_id = t.tag_id and dtl.destination_id = d.destination_id and ts.travel_id = 1 and ts.destination_id != d.destination_id
and t.tag_name in #{tagList}
group by d.destination_id, t.tag_name order by rand() limit 4 ) A

mapper.select:
SELECT      t.travel_name ,cost.total_cost,
            u.nick_name,
            CONCAT(t.start_date, '~' ,t.end_date) period
FROM        travel t , `user` u ,
            (
            SELECT sum(c.cost) total_cost
            from cost c
            WHERE c.travel_id = #{travelId}
            ) as cost
where       t.travel_id = #{travelId}
and         t.user_id = u.user_id
group by    t.travel_name;

mapper.update:
UPDATE  travel
set     travel_name = #{name}
WHERE   travel_id = #{id};

mapper.select:
SELECT      u.nick_name , c.state
from        companion c , `user` u
WHERE       c.travel_id = #{travelId}
and         u.user_id = c.companion_id
order by    c.state desc;

SELECT      name , t2.status
from        todo t2
WHERE       t2.travel_id = #{travelId}
order by    t2.reg_date desc
Limit       3;

SELECT      t2.todo_id , name , t2.status
from        todo t2
WHERE       t2.travel_id = #{travelId}
order by    t2.reg_date desc;

mapper.update:
UPDATE 	todo t
set 	t.status = #{status}
WHERE 	t.todo_id = #{todoId};

UPDATE 	todo t
set 	t.name  = #{name}
WHERE 	t.todo_id = #{todoId};

mapper.insert:
INSERT
into 	cost (name, cost, travel_id)
values 	(#{name}, #{cost}, #{travelId});

mapper.update:
UPDATE 	cost
set 	cost = #{cost} , name = #{name}
WHERE 	cost_id = #{costId};
